saphireRouter = require('./admin-routes/express-short-route.js'),
	express = require('express')

var saphireAdmin = {

	routes:function(app){

		app.use('/admin/css', express.static(__dirname+'/admin-views/styles/'));

		saphireRouter.create(app, {
			url:"admin",
			template:__dirname+"/admin-views/admin.jade"
		}/*data*/,{
			title:"Dashboard",
			purpose:"dashboard"
		})

		saphireRouter.create(app, {
			url:"admin/blocks",
			template:__dirname+"/admin-views/admin.jade"
		}/*data*/,{
			title:"Blocks",
			purpose:"blocks"
		})

		saphireRouter.create(app, {
			url:"admin/apperance",
			template:__dirname+"/admin-views/admin.jade"
		}/*data*/,{
			title:"Apperance",
			purpose:"apperance"
		})

	}

}

module.exports = saphireAdmin;
