var fs = require("fs")

module.exports = function(app){
	app.get("/", function(req,res){
		res.render("splash.jade")
	});
	app.get("/done", function(req,res){
		if(fs.readdirSync(__dirname).indexOf(__filename)){
			var data = {
				messageHeading:"Installation successfull",
				message:"Now you should remove saphire-installer, just run:'npm uninstall saphire-installer' and restart the node server to get started.",
				state:true
			}
		}
		else{
			var data = {
				messageHeading:"Failed installation",
				message:"You are seeing this message because somethings not right, please check check mongod to see if the status collection exists in your database.",
				state:false
			}
		}
		console.log(data)
		res.render("done.jade", {data:data})
	})
	app.get("/install", function(req,res){
		res.render("index.jade")
	})
}
