var fs = require('fs'),
		path = require('path');

module.exports = function (publicPath) {
	var themeRoot = path.normalize(publicPath+"themes");
	console.log(!fs.existsSync(themeRoot))
	if (!fs.existsSync(themeRoot)) {
		fs.mkdirSync(themeRoot);
		fs.mkdirSync(themeRoot + "/saphire");
		fs.writeFile(themeRoot + "/saphire/info.js", "//foo")
	}
	return themeRoot;
}
