var path = require("path"),
		fs = require("fs")

module.exports = function(){
	var systemTemplatesPath = path.resolve(__dirname, "../system/templates");
	var systemTemplates = fs.readdirSync(systemTemplatesPath);

	var systemPathDetail = []
	for(template in systemTemplates){
		systemPathDetail[template] = {name:systemTemplates[template],path:systemTemplatesPath+"/"+systemTemplates[template]}
	}
	return systemPathDetail;

}
