//after data has been extended access the data with this useData function(dataName,dataArr), this allows us to get around the problems caused by arrays of objects

module.exports = function(dataName,dataArr){
	for (var i=0; i<dataArr.length; i++)
		for (var name in dataArr[i]) {
			if(dataArr[i][dataName]){
				return dataArr[i][dataName]
			}
		}
}
