var loadMainPackageJSON = require("./main-package.js"),
		buildSaphireRegistry = require("./build-registry.js"),
		saphireRegistry = buildSaphireRegistry(loadMainPackageJSON())



module.exports = function(selector){
	var errM = "Could not find saphire module '"+ selector +"' please ensure that this module has been prefixed with saphire-<module name>";
	var installedList = saphireRegistry.toString();
	var regex = new RegExp("saphire-"+selector)
	if(regex.test(installedList)){return true;}
	else{
		console.log(errM)
	}
}
